document.addEventListener("DOMContentLoaded",(function(){const e=document.getElementById("imagenes"),t=document.getElementById("imagenPreviews");e.addEventListener("change",(e=>{t.innerHTML="";const n=e.target.files;for(let e=0;e<n.length;e++){const a=document.createElement("img"),d=document.createElement("canvas"),c=d.getContext("2d"),i=100,g=new Image;g.src=URL.createObjectURL(n[e]),g.onload=()=>{let e,n;g.width>i?(e=i,n=g.height*i/g.width):(e=g.width,n=g.height),d.width=e,d.height=n,c.drawImage(g,0,0,e,n),a.src=d.toDataURL("image/jpeg"),a.alt="Imagen seleccionada",t.appendChild(a)}}}))}));