async function submitForm(){const e=document.getElementById("nombre").value,t=document.getElementById("correo").value,n=document.getElementById("mensaje").value,r=document.getElementById("error-names"),o=document.getElementById("error-email"),m=document.getElementById("error-texto"),s=document.getElementById("success"),c={names:e,email:t,texto:n};try{const e=await axios.post("/suscribirse",c);r.innerHTML="",o.innerHTML="",m.innerHTML="",s.innerHTML="",e&&(s.innerHTML="<p>suscrito</p>")}catch(e){e.response.data.error&&e.response.data.error.forEach((e=>{console.log(e.path);const t=document.getElementById(`error-${e.path}`);t&&(t.innerHTML=e.msg)}))}}